mapper.select:
select

from payment


        and user_id = #{userId}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


order by create_time desc
limit #{pageSize} offset #{pageNum * pageSize - pageSize}

select COALESCE(sum(amount), 0)
from payment


        and user_id = #{userId}


        and payment_type = #{paymentType}


        and create_time >= #{startTime}


        and create_time <= #{endTime}
