mapper.select:
SELECT

FROM
reconciliation_batch
WHERE
reconciliation_date = #{date}
AND payment_type = #{paymentType}

SELECT

FROM
reconciliation_batch
WHERE
reconciliation_date >= #{startDate}
AND reconciliation_date <= #{endDate}

    AND payment_type = #{paymentType}


    AND reconciliation_status = #{status}

ORDER BY
reconciliation_date DESC

SELECT
FROM reconciliation_batch
WHERE payment_type = #{paymentType}
ORDER BY create_time DESC LIMIT #{limit}

SELECT COUNT(1) AS totalCount, SUM(CASE WHEN reconciliation_status = 2 THEN 1 ELSE 0 END) AS completedCount, SUM(CASE WHEN reconciliation_status = 3 THEN 1 ELSE 0 END) AS failedCount, SUM(CASE WHEN reconciliation_status = 1 THEN 1 ELSE 0 END) AS processingCount FROM reconciliation_batch WHERE 1=1
AND reconciliation_date >= #{startDate}
AND reconciliation_date <= #{endDate}
AND payment_type = #{paymentType}

SELECT

FROM
reconciliation_batch
WHERE 1=1

    AND reconciliation_date >= #{startDate}


    AND reconciliation_date <= #{endDate}


    AND payment_type = #{paymentType}


    AND reconciliation_status = #{status}

ORDER BY
create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(1) FROM reconciliation_batch
WHERE 1=1

    AND reconciliation_date >= #{startDate}


    AND reconciliation_date <= #{endDate}


    AND payment_type = #{paymentType}


    AND reconciliation_status = #{status}
