mapper.delete:
DELETE FROM cart
WHERE user_id = #{userId}
  AND product_id IN

          #{productId}

DELETE FROM cart
WHERE user_id = #{userId}
  AND product_id = #{productId}

mapper.update:
UPDATE cart
SET quantity = #{quantity},
    update_time = NOW()
WHERE user_id = #{userId}
  AND product_id = #{productId}

UPDATE cart
SET selected = #{selected},
    update_time = NOW()
WHERE user_id = #{userId}
  AND product_id = #{productId}

UPDATE cart
SET selected = #{selected},
    update_time = NOW()
WHERE user_id = #{userId}
  AND product_id IN

          #{productId}

mapper.select:
SELECT SUM(quantity) AS totalCount
FROM cart
WHERE user_id = #{userId}
