mapper.select:
SELECT * FROM takeout_review 
WHERE merchant_id = #{merchantId} 
ORDER BY create_time DESC

SELECT * FROM takeout_review 
WHERE user_id = #{userId} 
ORDER BY create_time DESC

SELECT * FROM takeout_review 
WHERE merchant_id = #{merchantId} 
AND rating >= 4 
ORDER BY create_time DESC 
LIMIT #{limit}

SELECT * FROM takeout_review 
WHERE merchant_id = #{merchantId}

    AND (

            JSON_CONTAINS(tags, #{tag}, '$')

    )

ORDER BY create_time DESC

mapper.update:
UPDATE takeout_review 
SET helpful_count = helpful_count + 1 
WHERE id = #{reviewId}
